CREATE TABLE tb_document (
    id integer NOT NULL PRIMARY KEY,
    title varchar(79) NOT NULL,
    id_author integer NOT NULL,
    text text NOT NULL,
    CONSTRAINT author_id_ct FOREIGN KEY(id_author) REFERENCES author(id)
);
CREATE TABLE tb_author (
    id integer NOT NULL PRIMARY KEY,
    name varchar(79) NOT NULL,
    UNIQUE (name)
);
CREATE TABLE tb_scores (
    id_author integer NOT NULL,
    id_document integer NOT NULL,
    score integer DEFAULT 0,
    PRIMARY KEY(id_author,id_document)
);
CREATE TABLE tb_references (
    id_referer_document INTEGER NOT NULL,
    id_refered_document INTEGER NOT NULL,
    PRIMARY KEY(id_referer_document, id_refered_document)
);
